            <div class="form-group" style="margin-bottom: 15px;">
              <label for="bez1">Bezbednosno pitanje</label>
              <input type="text" class="form-control" name="b1" id = "bez1" [(ngModel)]="safeQuestion" placeholder="Unesite bezbednosno pitanje">
              <br>
              <input type="text" class="form-control" name="b2" id = "b2" [(ngModel)]="safeResponse" placeholder="Unesite odgovor na pitanje">
            </div>
            <div class="form-group">
              <label for="ime">Ime</label>
              <input type="text" class="form-control" name="ime" id = "ime" [(ngModel)]="name" placeholder="Unesite ime">
            </div>
            <div class="form-group">
              <label for="prez">Prezime</label>
              <input type="text" class="form-control" name="prez" id = "prez" [(ngModel)]="lastName" placeholder="Unesite prezime">
            </div>
            <br>
            <div>Pol</div>
            <div class="form-check">
              <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" value="m" [(ngModel)]="gender">
