                <option value="strucna">Srednja stručna</option>
                <option value="osnovna">Osnovna škola</option>
              </select>
            </div>
            <br>
            <div class="form-group">
              <label for="slika" class="form-label">Slika</label>
              <input class="form-control" type="file" name="slika" id = "slika" (change)="onFileSelected($event)">
