import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private userService:UserService, private router:Router){}

  username:string = ""
  password:string = ""
  message:string = ""

  login(){
    this.userService.login(this.username, this.password).subscribe(
      (user)=>{
        if(user == null){
          this.message = "Korisnik ne postoji!"
          return;
        }else{
          localStorage.setItem("user", JSON.stringify(user));
          if(user.tip == 'gost'){
            this.router.navigate(['guestStart']);
          }else{
            this.router.navigate(['konobarStart']);
          }
        }
      }
    )
  }

}

<main id="main">
  <section id="about" class="about">
    <div class="container">
      <div class="row">
        <div class="col-lg-6 pt-4 pt-lg-0 order-2 order-lg-1 content" style="margin-top: 30px;">
          <h3>Ulogujte se</h3>
        </div>
      </div>
      <div class="row">
        <div class="col-lg-6 pt-4 pt-lg-0 order-2 order-lg-1 content">
        <form #loginF="ngForm">
          <br>
          <div class="form-group">
            <label for="exampleInputEmail1">Korisničko ime</label>
            <input type="email" class="form-control" id="exampleInputEmail1" name="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Unesite korisničko ime" required [(ngModel)]="username">
          </div>
          <br>
          <div class="form-group" style="margin-bottom: 15px;">
            <label for="exampleInputPassword1">Lozinka</label>
            <input type="password" class="form-control" id="exampleInputPassword1" name="exampleInputPassword1" placeholder="Unesite lozinku" required [(ngModel)]="password">
          </div>
          <a href="forgotPassword">Zaboravili ste lozinku?</a>
          <br>
          <br>
        </form>
        <button type="submit" class="btn get-started-btn" style="margin: 0;" [disabled]="loginF.invalid">Potvrdi</button>
      </div>
      </div>
      </div>
      <div style="margin-left: 80px; margin-top: 10px;color: crimson;">
        {{message}}
      </div>
  </section>
</main><!-- End #main -->
